import React from "react";
import "./Todo.css";
import {useDispatch} from 'react-redux';
import {completeTask, removeTask} from '../actions'

function Todo({ todo,location }) {

  const dispatch = useDispatch();

  return (
    <div>
    <div className="card-header list_item" style={{textDecoration : ((todo.completed)? "line-through" : "") , maxWidth:"40%"}}>
      {todo.text}
        <button 
          className="btn btn-success btn-sm" 
          data-testid = "finish-button" 
          onClick={e => {
            e.preventDefault();
            dispatch(completeTask(location));
          }}
        > 
        Finish 
        </button>
    </div>
    <button 
    className="btn btn-danger btn-sm" 
    data-testid = "remove-button" 
      onClick={e => {
      e.preventDefault();
      dispatch(removeTask(location));
    }}
  > 
  Remove
</button>
</div>
  );
};

export default Todo;
