import React from "react";
import "./App.css";
import {  useDispatch} from 'react-redux';
import {addTask,removeTask,completeTask} from './actions'

function Todo({ todo }) {

  const dispatch = useDispatch();

  return (
    <div className="todo" style={{textDecoration : ((todo.completed)? "line-through" : "")}}>
      {todo.text}
      {/* {textdecoration : (todo.completed? "Its completed" : todo.text)} */}
      <button onClick={() => dispatch(completeTask(todo.key))}> Finish </button>
    </div>
  );
};

export default Todo;
