const removetask = (state = ['Welcome to List'], action) => {
    switch(action.type){
        case 'REMOVEFROMLIST' : {
            const newTodos = [...state];
            newTodos.splice(action.payload, 1);
            return newTodos
        }
        default : 
            return state;
    }
}

export default removetask;
