const completeTask = (state = [{text : 'Welcome to List', completed:false},{text:'Lets begin!!', completed:false}, {text:"the journey", completed:true}], action) => {
    switch(action.type){
        case 'COMPLETETASK' : {
            console.log(state[action.payload])
            state[action.payload].completed = true;
            console.log(state)
            return state;
        }
        default : 
            return state
    }
}

export default completeTask;
