const removetask = (state = [{text : 'Welcome to List', completed:false},{text:'the journey', completed:false}], action) => {
    switch(action.type){
        case 'REMOVEFROMLIST' : {
            const newTodos = [...state];
            newTodos.splice(action.payload, 1);
            return newTodos
        }
        default : 
            return state;
    }
}

export default removetask;
