const completeTask = (state = [{text : 'Welcome to List', completed:false},{text:'Lets begin!!', completed:false}], action) => {
    switch(action.type){
        case 'COMPLETETASK' : {
            console.log(action.payload)
            state[action.payload].completed = true;
            console.log(state)
            return state;
        }
        default : 
            return state
    }
}

export default completeTask;
