import React from "react";
import "./Todo.css";
import {  useDispatch} from 'react-redux';
import {completeTask, removeTask} from './actions'

function Todo({ todo,location }) {

  const dispatch = useDispatch();

  return (
    <div className="card-header list_item" style={{textDecoration : ((todo.completed)? "line-through" : "") , maxWidth:"30%"}}>
      {todo.text}
        <button className="btn btn-success btn-sm"  onClick={e => {
          e.preventDefault();
          dispatch(completeTask(location));
        }}> 
        Finish 
        </button>
        <button className="btn btn-danger btn-sm"  onClick={e => {
          e.preventDefault();
          dispatch(removeTask(location));
        }}> 
        x
        </button>
    </div>
  );
};

export default Todo;
