const removetask = (state = [{text : 'Welcome to List', completed:false},{text:'Lets begin!!', completed:false}, {text:"the journey", completed:true}], action) => {
    switch(action.type){
        case 'DELETEFROMLIST' : {
            const newTodos = [...state];
            newTodos.splice(action.payload, 1);
            console.log(newTodos)
            return newTodos
        }
        default : 
            return state;
    }
}

export default removetask;
