const addTask = (state = [{text : 'Welcome to List', completed:false},{text:'Lets begin!!', completed:false}, {text:"the journey", completed:true}], action) => {
    switch(action.type){
        case 'ADDTOLIST' : {
            var newlist =  [...state , {text:action.payload , completed : false}]
            console.log(newlist)
            return newlist;
        }
        default : 
            return state
    }
}

export default addTask;
