const tasks = (state = [{text : 'Welcome to List', completed:false},{text:'Lets begin!!', completed:false}, {text:"the journey", completed:true}], action) => {
    switch(action.type){
        case 'ADDTOLIST' : {
            var newlist =  [...state , {text:action.payload , completed : false}]
            console.log(newlist)
            return newlist;
        }
        case 'DELETEFROMLIST' : {
            const newTodos = [...state];
            newTodos.splice(action.payload, 1);
            console.log(newTodos)
            return newTodos
        }
        case 'COMPLETETASK' : {
            console.log(state)
            state[action.payload].completed = true;
            return state;
        }
        default : 
            return state
    }
}

export default tasks;
