const addTask = (state = [{text : 'Welcome to List', completed:false},{text:'Lets begin!!', completed:false}, {text:"the journey", completed:true}], action) => {
    switch(action.type){
        case 'ADDTOLIST' : {
            return [...state , {text:action.payload , completed : false}]
        }
        default : 
            return state
    }
}

export default addTask;
