const addTask = (state = ['Welcome to List','Lets begin!!'], action) => {
    switch(action.type){
        case 'ADDTOLIST' : {
            return [...state , {text:action.payload , completed : false}]
        }
        default : 
            return state
    }
}

export default addTask;
