export const addToList = (task) => {
    return{
        type : "ADDTOLIST",
        payload : task
    }
}

export const deleteFromList = (index) =>{
    return {
        type : "DELETEFROMLIST",
        payload : index
    }
}

export const completeTask = (index) => {
    console.log(index)
    return {
        type : "COMPLETETASK",
        payload : index
    }
}
